mapper.select:
select

FROM invoice_head WHERE ORG_ID =#{head.orgId} 

   and bill_date like #{date} 

AND DOC_STATUS IN (1,3)

SELECT  , FROM invoice_head h
   INNER JOIN invoice_split_line s  ON  h.DOC_NUM=s.DOC_NUM AND s.GOLDTAX_CODE ='1'   
   WHERE s.group_STATUS = '1'


       AND h.org_id = #{orgId} 


         AND h.org_machine = #{orgMachine}  



       AND h.invoice_type =#{invoiceType}

   ORDER BY    h.DOC_DATE ASC ,s.DOC_NUM ASC,s.group_num ASC,s.DOC_LINE asc

SELECT  , FROM invoice_head h
  INNER JOIN invoice_split_line s  ON  h.DOC_NUM=s.DOC_NUM AND s.CANCEL_GDATE = "1"  
  WHERE s.group_STATUS = '3' 

      AND h.org_id = #{orgId} 


        AND h.org_machine = #{orgMachine}  



      AND h.invoice_type =#{invoiceType}

  ORDER BY    h.DOC_DATE ASC ,s.DOC_NUM ASC,s.group_num ASC,s.DOC_LINE asc

SELECT  , FROM invoice_head h
 INNER JOIN invoice_split_line s  ON  h.DOC_NUM=s.DOC_NUM AND s.GOLDTAX_CODE !='1'   
 WHERE s.group_STATUS = #{docStatus} 

     AND h.org_id = #{orgId} 


       AND h.org_machine = #{orgMachine}  


     AND h.invoice_type =#{invoiceType}

 ORDER BY    h.DOC_DATE ASC ,s.DOC_NUM ASC,s.group_num ASC,s.DOC_LINE asc
