mapper.select:
SELECT    FROM  `t-invoice` WHERE 1=1

           and ORG_ID=#{orgId}


           and INVOICE_TYPE = #{invoiceType}


           and CUST_NAME=#{custName}


           and CUST_ID_BILL=#{custIdBill}


           and ISSYNC=#{issync}


           and DOC_NUM=#{docNum}


           and  DOC_DATE >= #{billDateStart}


           and DOC_DATE <= #{billDateEnd}



             AND
             ( (bill_gdate   >= #{billGdateStart1}   and  length(trim(bill_gdate)) =19) or (bill_gdate  >=  #{billGdateStart}    and  length(trim(bill_gdate)) =14) ) 


           and  
           ( (bill_gdate   <= #{billGdateEnd1}   and  length(trim(bill_gdate)) =19) or (bill_gdate  <=  #{billGdateEnd}    and  length(trim(bill_gdate)) =14) ) 



           and GOLDTAX_NUM >= #{goldtaxNumStart}


           and GOLDTAX_NUM <= #{goldtaxNumEnd}


           and GOLDTAX_CODE = #{goldtaxCode}


           and GROUP_STATUS = #{groupStatus}


 GROUP BY doc_num,GROUP_num 
 order by  BILL_GDATE desc,BILL_DATE desc

SELECT SUM(ZAMOUNT_WSJ) AS wsj ,SUM(ZAMOUNT_SEJ) AS sej FROM `t-invoice` WHERE GROUP_STATUS =2
and org_id =#{orgId}
and ( bill_gdate LIKE #{mouth1} OR bill_gdate LIKE #{mouth2})

SELECT  DOC_NUM,GROUP_NUM FROM invoice_split_line WHERE  GROUP_STATUS='2'

  and org_id =#{orgId}

 AND doc_num = #{docNum}  GROUP BY  GROUP_NUM, DOC_NUM

SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h WHERE s.group_status='2' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND (s.bill_gdate LIKE #{day1} OR s.bill_gdate LIKE #{day2}) GROUP BY s.GROUP_NUM,s.DOC_NUM

SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h WHERE s.group_status='9' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND (s.CANCEL_GDATE LIKE #{day1} OR s.CANCEL_GDATE LIKE #{day2}) GROUP BY s.GROUP_NUM,s.DOC_NUM

     SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h  
     WHERE  s.group_status in ('1','3')  and h.DOC_NUM =s.DOC_NUM 


    and   h.org_Id=#{orgId} 

AND   s.BILL_DATE <= #{day2} 
      AND   s.BILL_DATE >= #{start2}  GROUP BY s.GROUP_NUM,s.DOC_NUM

     SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h  
     WHERE  s.group_status in ('1')  and h.DOC_NUM =s.DOC_NUM  


  and      h.org_Id=#{orgId} 


AND   s.BILL_DATE <= #{day2}  AND   s.BILL_DATE >= #{start2}  
     GROUP BY s.GROUP_NUM,s.DOC_NUM

SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h WHERE s.group_status in ('3') and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND s.BILL_DATE <= #{day2} AND s.BILL_DATE >= #{start2} GROUP BY s.GROUP_NUM,s.DOC_NUM

     select s.GROUP_STATUS as 'key', count(distinct s.DOC_NUM, s.GROUP_NUM) as 'value'
     from invoice_split_line s,
          invoice_head h
     where h.DOC_NUM = s.DOC_NUM


and      h.org_Id=#{orgId} 

       and s.BILL_DATE >= #{dateStart}
     group by s.GROUP_STATUS

    SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h  
    WHERE  s.dayin in ('1')   and h.DOC_NUM =s.DOC_NUM  

 and      h.org_Id=#{orgId} 

AND   s.BILL_DATE <= #{day2}  AND   s.BILL_DATE >= #{start2}  
    GROUP BY s.GROUP_NUM,s.DOC_NUM

SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h WHERE s.dayin in ('0') and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND s.BILL_DATE <= #{day2} AND s.BILL_DATE >= #{start2} GROUP BY s.GROUP_NUM,s.DOC_NUM

    SELECT DOC_NUM FROM  invoice_head h  
    WHERE  h.ISSYNC = '2'    

 and      h.org_Id=#{orgId} 

AND   h.BILL_DATE <= #{day2}  AND   h.BILL_DATE >= #{start2}

     SELECT DOC_NUM FROM  invoice_head h  
     WHERE  h.ISSYNC != '2'    

  and      h.org_Id=#{orgId} 

AND   h.BILL_DATE <= #{day2}  AND   h.BILL_DATE >= #{start2}

SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h WHERE s.group_status='2' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND ( (s.bill_gdate <= #{day1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate <= #{day2} and length(trim(s.bill_gdate)) =14) ) AND ( (s.bill_gdate >= #{start1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate >= #{start2} and length(trim(s.bill_gdate)) =14) ) GROUP BY s.GROUP_NUM,s.DOC_NUM

SELECT GROUP_NUM FROM invoice_split_line s,invoice_head h WHERE s.group_status='9' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND ( (s.CANCEL_GDATE <= #{day1} and length(trim(s.CANCEL_GDATE)) =19) or (s.CANCEL_GDATE <= #{day2} and length(trim(s.CANCEL_GDATE)) =14) ) and ( (s.CANCEL_GDATE >= #{start1} and length(trim(s.CANCEL_GDATE)) =19) or (s.CANCEL_GDATE >= #{start2} and length(trim(s.CANCEL_GDATE)) =14) ) GROUP BY s.GROUP_NUM,s.DOC_NUM

SELECT sum(s.ZAMOUNT_HSJ) FROM invoice_split_line s,invoice_head h WHERE s.group_status='2' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND s.ZAMOUNT_HSJ >= 0 AND ( (s.bill_gdate <= #{end1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate <= #{end2} and length(trim(s.bill_gdate)) =14) ) AND ( (s.bill_gdate >= #{start1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate >= #{start2} and length(trim(s.bill_gdate)) =14) )

SELECT sum(s.ZAMOUNT_HSJ) FROM invoice_split_line s,invoice_head h WHERE s.group_status='2' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND s.ZAMOUNT_HSJ <= 0 AND ( (s.bill_gdate <= #{end1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate <= #{end2} and length(trim(s.bill_gdate)) =14) ) AND ( (s.bill_gdate >= #{start1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate >= #{start2} and length(trim(s.bill_gdate)) =14) )

SELECT sum(s.ZAMOUNT_SEJ) FROM invoice_split_line s,invoice_head h WHERE s.group_status='2' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND ( (s.bill_gdate <= #{end1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate <= #{end2} and length(trim(s.bill_gdate)) =14) ) AND ( (s.bill_gdate >= #{start1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate >= #{start2} and length(trim(s.bill_gdate)) =14) )

SELECT sum(s.ZAMOUNT_HZHS) FROM invoice_split_line s,invoice_head h WHERE s.group_status='2' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND ( (s.bill_gdate <= #{end1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate <= #{end2} and length(trim(s.bill_gdate)) =14) ) AND ( (s.bill_gdate >= #{start1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate >= #{start2} and length(trim(s.bill_gdate)) =14) )

SELECT sum(s.ZAMOUNT_HSJ) FROM invoice_split_line s,invoice_head h WHERE h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND ( (s.bill_gdate <= #{end1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate <= #{end2} and length(trim(s.bill_gdate)) =14) ) AND ( (s.bill_gdate >= #{start1} and length(trim(s.bill_gdate)) =19) or (s.bill_gdate >= #{start2} and length(trim(s.bill_gdate)) =14) )

SELECT sum(s.ZAMOUNT_HSJ) FROM invoice_split_line s,invoice_head h WHERE s.group_status='9' and h.DOC_NUM =s.DOC_NUM
and h.org_Id=#{orgId}
AND ( (s.CANCEL_GDATE <= #{end1} and length(trim(s.CANCEL_GDATE)) =19) or (s.CANCEL_GDATE <= #{end2} and length(trim(s.CANCEL_GDATE)) =14) ) and ( (s.CANCEL_GDATE >= #{start1} and length(trim(s.CANCEL_GDATE)) =19) or (s.CANCEL_GDATE >= #{start2} and length(trim(s.CANCEL_GDATE)) =14) )

select 

from invoice_split_line
where DOC_NUM = #{docNum,jdbcType=VARCHAR}
  and DOC_LINE = #{docLine,jdbcType=VARCHAR}

mapper.delete:
delete from invoice_split_line
where DOC_NUM = #{docNum,jdbcType=VARCHAR}
  and DOC_LINE = #{docLine,jdbcType=VARCHAR}
